package javatesting;

public class Loop4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Display number from 1 to 10 ,show only even numbers
		// perfect way to find even numbers
		for (int i = 1; i <= 10; i++) {
			if ((i % 2) == 0)

			{
				System.out.println("value of i" + i);
			}
		}
	}
}
