package javatesting;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String color="Black";
switch(color)
{
	case"Red":
		System.out.println("DANGER");
		break;
	case"Blue":
		System.out.println("OCEAN");
	case"Black":
		System.out.println("FUNDAMENTAL COLOR");
		break;
		default:
			System.out.println("INVALIED COLOR");
}
	}

}
